import React, { useState, useEffect } from "react";
import axios from "axios"
import { Link } from "react-router-dom";

const Sapatos = () =>{
    const [products, setProducts] = useState([]);
    const [data, setData] = useState([]);
    const [id, setId ] = useState(3);
    //aqui est치 buscando a lista de categorias do banco de dados.
  useEffect(() => {
    (async function sendProducts() {
        try {
            const response = await axios.get('http://localhost:8888/api/V1/categories/list');
            setProducts(response.data.items);
        } catch (err) {
            console.error(err);
        }
    })();
}, []); 
 //aqui est치 buscando todos os produtos do banco de dados
useEffect(() => {
  (async function sendListProducts() {
      try {
          const list = await axios.get(`http://localhost:8888/api/V1/categories/${id}`);
          setData(list.data.items);
      } catch (err) {
          console.error(err);
      }
  })();
}, [data]); 
    return (
         <div className="row d-flex justify-content-around ">
        {data.map((value, i) => {
            return (
                <div className="col-sm-2 m-4"key={i}>
                <Link to={`/camisetas/${value.path}`}>
                    <img className="" src={value.image} alt={`Imagem produto ${value.name}`}/>
                  </Link>
                   <h3>{value.name}</h3>
                   <span>{value.price}</span>
                </div>
            )
        })}
        </div>
    )
}

export default Sapatos
