import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import LogoImg from "../media/logo_webjump.png"
import axios from 'axios'

const Navbar = () => {
  const [products, setProducts] = useState([]);
  const [data, setData] = useState([]);
  const [id, setId ] = useState(1);

  //aqui está buscando a lista de categorias do banco de dados.
  useEffect(() => {
      (async function sendProducts() {
          try {
              const response = await axios.get('http://localhost:8888/api/V1/categories/list');
              setProducts(response.data.items);
          } catch (err) {
              console.error(err);
          }
      })();
  }, []); 

  useEffect(() => {
    (async function sendListProducts() {
        try {
            const list = await axios.get(`http://localhost:8888/api/V1/categories/${id}`);
            setData(list.data.items);
        } catch (err) {
            console.error(err);
        }
    })();
}, [data]); 

    return (

        <div>
         {/*Aqui está interando sobre o array de categorias e renderizando os mesmo*/}
         {products.map((product, i) => {
            return (
                <div key={i}>
                   <ul>
                        <li><button onClick={() => setId(product.id)}>{product.name}</button></li>
                   </ul>
                </div>
            )
        })}
        {/*Aqui está interando sobre o array de produtos e renderizando os mesmos*/}
        {data.map((value, i) => {
                    return (
                        <div key={i}>
                           <h3>{value.name}</h3>
                           <span>{value.price}</span>
                        </div>
                    )
                })}
          <div className="upperNavBar d-flex flex-row-reverse">
            <p className="linksUpperNavBar"><Link className="linksUpperNavBar" to="/acessar"><b>Acesse sua Conta</b></Link> ou <Link to="/cadastrar"><b>Cadastre-se</b></Link></p>
          </div>

          <header className="header">
            <nav className="navbar navbar-light bg-light">
              <Link className="navbar-brand" to="/">
                <img src={LogoImg} alt="WebJump logomarca"/>
              </Link>

              <div className="navSearch" >
                 <form className="form-inline">
                  <input className="form-control" type="text" placeholder="Search" aria-label="Procure aqui..."/>
                  <button className="btn btn-outline-success" type="submit">BUSCAR</button>
                </form>
              </div>
            </nav>
          </header>

          <div className="navbarProducts  ">
          <nav className="navbar navbar-expand-sm  d-flex align-items-center">
        <ul className="navProducts d-flex align-items-center">
        <li className="nav-item">
          <Link className="navLink " to="/">PÁGINA INICIAL</Link>
        </li>
        <li className="nav-item">
          <Link className="navLink" to="/camisetas">CAMISETAS</Link>
        </li>
        <li className="nav-item">
          <Link className="navLink" to="/calcas">CALÇAS</Link>
        </li>
        <li className="nav-item">
          <Link className="navLink" to="/sapatos">SAPATOS</Link>
        </li>
        <li className="nav-item">
          <Link className="navLink " to="/contato">CONTATO</Link>
        </li>
      </ul>
        </nav>
        </div>
        </div>

    )
}

export default Navbar;
