import React, { useState, useEffect } from "react";
import axios from "axios"
import { Link } from "react-router-dom";

const Sapatos = () =>{
    const [products, setProducts] = useState([]);
    const [data, setData] = useState([]);
    const [id, setId ] = useState(3);
    //aqui está buscando a lista de categorias do banco de dados.
  useEffect(() => {
    (async function sendProducts() {
        try {
            const response = await axios.get('http://localhost:8888/api/V1/categories/list');
            setProducts(response.data.items);
        } catch (err) {
            console.error(err);
        }
    })();
}, []); 
 //aqui está buscando todos os produtos do banco de dados
useEffect(() => {
  (async function sendListProducts() {
      try {
          const list = await axios.get(`http://localhost:8888/api/V1/categories/${id}`);
          setData(list.data.items);
      } catch (err) {
          console.error(err);
      }
  })();
}, [data]); 
    return (
            <div className="main d-flex flex-row">
                <div className="navbarLateral ">
                    <h3>FILTRE POR</h3>
                    <h4>CATEGORIAS</h4>
                    <ul>
                        <li>Roupas</li>
                        <li>Sapatos</li>
                        <li>Acessórios</li>
                    </ul>
                    <h4>CORES</h4>
                    <ul className="coresLista d-flex flex-row" >
                        <li className="corVermelha"></li>
                        <li className="corLaranja"></li>
                        <li className="corCiano"></li>
                    </ul>
                    <h4>TIPO</h4>
                    <ul>
                        <li>Corrida</li>
                        <li>Caminhada</li>
                        <li>Casual</li>
                        <li>Social</li>
                    </ul>

                </div>
                <div className="card-deck ">
         {data.map((value, i) => {
            return (
                <div className="col-sm-3">
            <div className="card">

            <Link to={`/camisetas/${value.path}`}>
                    <img className="card-img-top" src={value.image} alt={`Imagem produto ${value.name}`}/>
                  </Link>

                <div className="card-body"key={i}>

                   <h3 className="card-title">{value.name}</h3>
                   <span>R$ {value.price}</span>
                </div>
                </div>
                </div>
            )
        })}
        </div>
            </div>

    )
}

export default Sapatos
